import React, { useEffect, useState } from 'react';
import { Delete, Read } from '../../APIServices/CRUDServices';
import { errorToast, successToast } from '../../Helper/ValidationHelper';
import { useHistory } from 'react-router-dom';


const ListTable = (props) => {
    let [DataList, SetDataList] = useState([]);
    const history = useHistory();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        Read().then((Result) => {
            SetDataList(Result);
        })
        .catch((error) => {
            console.error('Error fetching data:', error);
        });
    }

    const DeleteItem = (id) => {
        Delete(id).then((result) => {
            if (result === true) {
                successToast("delete success");
                fetchData(); // Fetch data again after deletion
                 props.history.push("/"); // Assuming you want to redirect to home page after deletion
            } else {
                errorToast("delete fail");
            }
        });
    }

    const UpdateItem = (id) => {
        // Add your update logic here
    }

    return (
        <div>
            <table className='table' border={2} >
                <thead>
                    <tr>
                        <th>Product Name</th>
                        <th>Product Code</th>
                        <th>Image</th>
                        <th>Unit Price</th>
                        <th>Qty</th>
                        <th>Total Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {Array.isArray(DataList) && DataList.length > 0 ? (
                        DataList.map((item, i) => (
                            <tr key={i}>
                                <td>{item.ProductName}</td>
                                <td>{item.ProductCode}</td>
                                <td><img className='list-img' src={item.Img} width={50} alt='' /></td>
                                <td>{item.UnitPrice}</td>
                                <td>{item.Qty}</td>
                                <td>{item.TotalPrice}</td>
                                <td><button onClick={() => DeleteItem(item.id)}>Delete</button></td>
                                <td><button onClick={() => UpdateItem(item.id)}>Update</button></td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7">No data available</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ListTable;
