import React, { useEffect, useState } from 'react';
import { Delete, Read } from '../../APIServices/CRUDServices';
import { errorToast, successToast } from '../../Helper/ValidationHelper';
import { useHistory } from 'react-router-dom';


const ListTable = (props) => {
    const history = useHistory();
    let [DataList, SetDataList] = useState([]);
    
    useEffect(() => {
        Read().then((Result) => {
            
                SetDataList(Result);
           
        })
        .catch((error) => {
            console.error('Error fetching data:', error);
        });
    }, [])

    const DeleteItem = (id) => {
        Delete(id).then((result) => {
            if (result === true) {
                successToast("Delete success");
                // Refresh data after successful delete
                Read().then((result) => {
                    SetDataList(result);
                }).catch((error) => {
                    console.error('Error fetching data:', error);
                });
            } else {
                errorToast("Delete fail");
            }
        }).catch((error) => {
            console.error('Error deleting item:', error);
            errorToast("Delete error");
        });
    }
    

    const UpdateItem=(id)=>{
          alert(id);
          props.history.push("/update"+id)
    }

    return (
        <div>
            <table className='table' border={2} >
                <thead>
                    <tr>
                        <th>Product Name</th>
                        <th>Product Code</th>
                        <th>Image</th>
                        <th>Unit Price</th>
                        <th>Qty</th>
                        <th>Total Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                {Array.isArray(DataList) && DataList.length > 0 ? (
                        DataList.map((item, i) => (
                            <tr key={i}>
                                <td>{item.ProductName}</td>
                                <td>{item.ProductCode}</td>
                                <td><img className='list-img' src={item.Img} width={50} alt='' /></td>
                                <td>{item.UnitPrice}</td>
                                <td>{item.Qty}</td>
                                <td>{item.TotalPrice}</td>
                                <td><button onClick={DeleteItem.bind(this,item._id)}>Delete</button></td>
                                <td><button onClick={UpdateItem.bind(this,item._id)}>Update</button></td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7">No data available</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ListTable;
